import React from 'react';
import './darkMode.scss'
import { useState, useEffect } from 'react';
import Sun from '../img/sun.png';
import Moon from '../img/moon.png';
import { useContext } from 'react';
import { ThemeContext } from '../Context/ThemeContext';


function DarkMode(props) {

    const [circleToggle, setcircleToggle] = useState('0');
   
    const {theme, toggleTheme,toggleLogo,setTheme} = useContext(ThemeContext);
    const novoTema = setTheme;


 


    useEffect((novoTema) => {

      const themeSalvo = localStorage.getItem('theme');
      if (themeSalvo) {
        setTheme(themeSalvo);
      }

      localStorage.setItem('theme', theme);
    }, [theme]);


    

    const handleClick = () => {
        setcircleToggle(!circleToggle);
        toggleTheme();
        toggleLogo();
        
      };
    
      const containerStyle = {        
        transform: circleToggle ? 'translateX(1%)' : 'translateX(112%)',        
      };  

      const [icon,setIcon] = useState(Sun);
      useEffect((icon) => {
        
        if (icon === Sun) {
          setIcon(Moon);
        } else {
          setIcon(Sun);
        }
      }, [theme]);



    return ( 

        <div className='button--container' >  
            <div className='button--border' > 
            <div className='button--circle' style={containerStyle} onClick={handleClick}>{props.children}   
                <img src={icon} alt="icon-darkmode"/>
            </div>
            </div>
        </div>

     );
}

export default DarkMode;
