import logo from './logo.svg';
import './App.scss';
import Home from './Home'
import DarkMode from './Navbar/darkMode';
import Navbar from './Navbar';
import { useState } from 'react';


function App(props) {

  const bgLight = {
    backgroundColor: 'fff',   
  }  
  const bgDark = { 
    backgroundColor:'#000',
    
       }

  const [darkMode, setDarkMode] = useState(bgLight);

  
   

  function changeMode () {

    if (darkMode === bgLight ) {
      setDarkMode(bgDark);
     } 
     
     else if (darkMode === bgDark) {
      setDarkMode(bgLight);
     }
  
    

  }
  
  const borders = {
    border: '1px solid red'
  }
 
  

  return (

    <div className="App" style={darkMode}>


      <Navbar> <div style={borders} onClick={changeMode}> <DarkMode/></div> </Navbar>
      
      <Home/>
      

    </div>
  );
}
export default App;
