import React from 'react';
import './App.scss';
import Home from './Home'
import Navbar from './Navbar';
import About from './About-me';
import Jobs from './Jobs';
import Projects from './Projects'
import Contacts from './Contacts'
import DarkMode from './Navbar/darkMode';
import { useContext, useRef ,useEffect} from 'react';
import { ThemeContext } from './Context/ThemeContext';




function App() {

  const {logoToggle} = useContext(ThemeContext)
  document.title = 'Página Inicial | Portfólio André Sampaio';
  const meuPontoRef = useRef();

  useEffect(() => {
    // Obter a posição do elemento referenciado
    const meuPontoPosicao = meuPontoRef.current.getBoundingClientRect();
    // Armazenar a posição em uma variável ou estado
    // Exemplo: setPosicao({ top: meuPontoPosicao.top, left: meuPontoPosicao.left });
  }, []);



  return (

    <div className="App"> 

      {/* <Grids> </Grids>    */}
    <div>
 

      <Navbar> <DarkMode> <div className='button-container--app'></div> </DarkMode> </Navbar>
      <Home>  
      <img className='logo-react' src={logoToggle} alt="Logo-React-Animado"></img>    
      
    

      </Home>
      <About> </About>
    
      <Projects> </Projects>
      <Contacts> </Contacts>
      <Jobs>  </Jobs>
     
      
      </div>
     
    </div>
  );
}
export default App;
