import React from 'react';
import './Home.scss';
import Balloons from './Balloons';
import GitHub from '../img/github.png';
import Linkedin from '../img/linkedin.png';
import Resume from '../img/resume.png';




function Home(props) {

   let url = [ 
    'https://github.com/AndreMicael',
    'https://www.linkedin.com/in/andremsampaio/'
   ]

    const gitButton = document.querySelector('#btnGit');

    function openInNewTab(url) {
        const win = window.open(url, '_blank')
        win.focus()
    }
    
    gitButton.addEventListener('click', () => {

        openInNewTab(url[0]);

    })


    return ( 

        <section className='home--container no-select'>
           
          <div className='logo--side'> 
         
           {props.children}           

          </div>
          <div className='info--side'> 


           <div className='info--text'>
               
               <div className='info--line'> <div className='info--column'> <p> Prazer, me chamo André </p>  <h2> Front-End </h2>  </div> 
               <div className='info--button'>
               
               
               <div className='button--git' id='btnGit'>              

                   <Balloons> <p className='button--text'>  Conheça meus projetos</p></Balloons>
                   <img src={GitHub} alt="GitHub"></img>

                  </div>
                  

               <div className='button--linkedin'> 
              
               <Balloons> <p className='button--text'>Vamos nos<br/> conhecer?</p></Balloons>
                   <img src={Linkedin} alt="Linkedin"></img>
               
               </div> 
               <div className='button--cv'> 
               
               <Balloons><p className='button--text'>Ver Meu<br/> Curriculo  </p></Balloons>
                   <img src={Resume} alt="Curriculo"></img>

                </div> 
               </div>
               
               </div>
               <h1> Developer </h1>
               <p className='info--description'> Tenho 25 anos, sou desenvolvedor com experiência em React, HTML, CSS para aplicações Web.</p>
           </div>

          </div>

          

        </section>

        
     );
}

export default Home;
