import React from 'react';
import './App.scss';
import Home from './Home'
import Navbar from './Navbar';
import About from './About-me';
import Jobs from './Jobs';
import Projects from './Projects'
import Contacts from './Contacts'
import DarkMode from './Navbar/darkMode';
import { useContext, useState} from 'react';
import { ThemeContext } from './Context/ThemeContext';
import { useLocation, Link } from 'react-router-dom';





function App() {

  const {logoToggle} = useContext(ThemeContext)
  document.title = 'Página Inicial | Portfólio André Sampaio';


  const location = useLocation();
  const [scrollPosition, setScrollPosition] = useState(0);

  const handleLinkClick = () => {
    setScrollPosition(window.pageYOffset);
  };




  return (

    <div className="App"> 
      
      {/* <Grids> </Grids>    */}
    <div>
 

      <Navbar> <DarkMode> <div className='button-container--app'></div> </DarkMode> </Navbar>
      <Home>  
      <img className='logo-react' src={logoToggle} alt="Logo-React-Animado"></img>    
      
    

      </Home>
      <About> </About>
    
      <Projects>  <Link to={{ pathname: '/outra-pagina', state: { scrollPosition } }} onClick={handleLinkClick}> </Link>  </Projects>
      <Contacts> </Contacts>
      <Jobs>  </Jobs>
     
      
      </div>
     
    </div>
  );
}
export default App;
