import './App.scss';
import Home from './Home'
import DarkMode from './Navbar/darkMode';
import Navbar from './Navbar';
import LogoReactD from './img/logo2.svg';
import LogoReactL from './img/logo.svg';
import React from 'react';
import { useState } from 'react';


function App(props) {

  let [darkMode, setDarkMode] = useState('modo-claro');    
  let [logoReact, setLogoReact] = useState(LogoReactL);
  

  const changeMode = () =>  {
          if (darkMode === 'modo-claro'){
            setDarkMode('modo-escuro');
            setLogoReact(LogoReactL);     
                    
          }
          else {
            setDarkMode('modo-claro');
            setLogoReact(LogoReactD); 
          }
    }   
  

  return (

    <div className="App"> 
    <div className={darkMode}>


      <Navbar> <DarkMode> <div className='button-container--app' onClick={changeMode}></div> </DarkMode> </Navbar>
      <Home> {logoReact} </Home>
      
      </div>
    </div>
  );
}
export default App;
