import logo from './logo.svg';
import './App.scss';
import Home from './Home'
import DarkMode from './Navbar/darkMode';
import Navbar from './Navbar';
import { useState } from 'react';


function App(props) {

  const bgLight = {
    backgroundColor: '000',
    filter: 'invert(100%)',

  }
  
  const bgDark = { 
    backgroundColor:'#fff',
    filter: 'invert(100%)',
    
   }

  const [darkMode, setDarkMode] = useState(bgLight);

  const changeMode = () => {

    //condição ? valor_se_verdadeiro : valor_se_falso;

    darkMode ? setDarkMode(bgDark) : setDarkMode(bgLight);

  };
  
 
  

  return (

    <div className="App" style={bgDark}>


      <Navbar> <div onClick={changeMode}> <DarkMode/></div> </Navbar>
      
      <Home/>
      

    </div>
  );
}
export default App;
